import React, { useState } from "react";
import { withRouter, Redirect } from "react-router-dom";

import ReactPlayer from "react-player";
import quizOneBackground from "../visuals/quiz/q1_background.mp4";
import title from "../images/quiz/q1/title.png";
import placeholder from "../images/quiz/q1/background_placeholder.png";

function QuestionOne() {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("h");
    setSubmitted(true);
  };
  if (submitted) {
    return (
      <Redirect
        push
        to={{
          pathname: "/question-two",
        }}
      />
    );
  }
  return (
    <div className="questionContainer">
      <ReactPlayer
        className="react-player quiz-video"
        url={quizOneBackground}
        width="100%"
        height="100%"
        controls={false}
        loop={true}
        playing={true}
      />
      <div className="content questionThree_container">
        <div className="">
          <img src={title} alt="" className="question_title" />
        </div>
        <form onSubmit={handleSubmit}>
          <label>
            <input
              type="text"
              placeholder="Write here"
              className="q1_text"
              id="demo"
            />
          </label>
        </form>
      </div>
    </div>
  );
}

export default withRouter(QuestionOne);
